public class MergeSort {

	/**
	 * The main method gets input from the user and calls the merge sort method.
	 * 
	 * @param args
	 */
	public static void main(String[] args) {
		Scanner input = new Scanner(System.in);

		// Get input from the user
		System.out.print("Enter the length of the array: ");
		int length = input.nextInt();

		if (length < 0) {
			System.out.println("Length of the array must be a non-negative number.");
			return;
		}

		int[] array = new int[length];

		for (int I = 0; I < length; i++) {
			System.out.print("Enter a non-negative integer: ");
			array[i] = input.nextInt();

			if (array[i] < 0) {
				System.out.println("Array can only contain non-negative integers.");
				return;
			}
		}

		// Call the merge sort method
		mergeSort(array, 0, array.length - 1);

		// Output the sorted array to the console
		System.out.println("Sorted array: " + Arrays.toString(array));

		input.close();
	}

	/**
	 * The merge sort method sorts an array of non-negative integers in ascending order.
	 * 
	 * @param array: the array to be sorted
	 * @param left: the left-most index of the sub-array
	 * @param right: the right-most index of the sub-array
	 */
	public static void mergeSort(int[] array, int left, int right) {
		if (left < right) {
			// Find the middle point
			int middle = (left + right) / 2;

			// Sort the left and right halves of the array
			mergeSort(array, left, middle);
			mergeSort(array, middle + 1, right);

			// Merge the sorted halves
			merge(array, left, middle, right);
		}
	}

	/**
	 * The merge method merges two sorted sub-arrays of an array.
	 * 
	 * @param array: the array containing the two sub-arrays
	 * @param left: the left-most index of the left sub-array
	 * @param middle: the right-most index of the left sub-array and left-most index of the right sub-array
	 * @param right: the right-most index of the right sub-array
	 */
	public static void merge(int[] array, int left, int middle, int right) {
		// Find the sizes of the two sub-arrays
		int n1 = middle - left + 1;
		int n2 = right - middle;

		// Create temporary arrays
		int[] leftArray = new int[n1];
		int[] rightArray = new int[n2];

		// Copy the elements of the two sub-arrays into the temporary arrays
		for (int I = 0; I < n1; i++) {
			leftArray[i] = array[left + i];
		}
		for (int j = 0; j < n2; j++) {
			rightArray[j] = array[middle + 1 + j];
		}

		// Merge the temporary arrays back into the original array
		int I = 0, j = 0, k = left;
		while (i < n1 && j < n2) {
			if (leftArray[i] <= rightArray[j]) {
				array[k] = leftArray[i];
				i++;
			} else {
				array[k] = rightArray[j];
				j++;
			}
			k++;
		}

		// Copy the remaining elements of the left sub-array into the original array
		while (i < n1) {
			array[k] = leftArray[i];
			i++;
			k++;
		}

		// Copy the remaining elements of the right sub-array into the original array
		while (j < n2) {
			array[k] = rightArray[j];
			j++;
			k++;
		}
	}

}
