void rotateArray(int arr[], int n, int p) {
    // create a temporary array to store rotated elements
    int temp[p];
    for (int I = 0; I < p; i++) {
        temp[i] = arr[n-p+i];
    }
    // shift the elements to the right
    for (int I = n-p-1; I >= 0; i--) {
        arr[i+p] = arr[i];
