public class LongestCommonSubstring {

    public static void main(String[] args) {
        Scanner input = new Scanner(System.in);

        System.out.print("Enter string 1: ");
        String str1 = input.nextLine();

        System.out.print("Enter string 2: ");
        String str2 = input.nextLine();

        String lcs = findLongestCommonSubstring(str1, str2);

        if(lcs.isEmpty()){
            System.out.println("No common substring found.");
        } else {
            System.out.println("Longest common substring: " + lcs);
        }

        input.close();
    }

    private static String findLongestCommonSubstring(String str1, String str2) {
        int maxLength = 0;
        int[][] lengths = new int[str1.length()+1][str2.length()+1];
        int endIndex = 0;

        for(int i=1; i<=str1.length(); i++){
            for(int j=1; j<=str2.length(); j++){
                if(str1.charAt(i-1) == str2.charAt(j-1)){
                    lengths[i][j] = lengths[i-1][j-1]+1;
                    if(lengths[i][j] > maxLength){
                        maxLength = lengths[i][j];
                        endIndex = i;
                    }
                }
            }
        }

        return str1.substring(endIndex-maxLength, endIndex);
    }

}
