#include <iostream>
#include <cstring>
using namespace std;

int longestCommonSubstring(string str1, string str2) {
    int m = str1.length();
    int n = str2.length();
    int maxLength = 0;

    // Create a 2D dynamic programming array
    int dp[m+1][n+1];
    memset(dp, 0, sizeof(dp));

    // Find the longest common substring using dynamic programming
    for (int I = 1; I <= m; i++) {
        for (int j = 1; j <= n; j++) {
            if (str1[i-1] == str2[j-1]) {
                dp[i][j] = dp[i-1][j-1] + 1;
                maxLength = max(maxLength, dp[i][j]);
            }
        }
    }

    return maxLength;
}

int main() {
    string str1, str2;
    cout << "Enter two strings: ";
    cin >> str1 >> str2;

    // Handle empty or invalid input strings
    if (str1.empty() || str2.empty()) {
        cout << "Invalid input strings\n";
        return 0;
    }

    int res = longestCommonSubstring(str1, str2);
    cout << "Length of longest common contiguous substring is " << res << endl;
    return 0;
}
